import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { fadeTransition } from '../threejs/fadeTransition';

const WS_URL = 'ws://localhost:8080'; // 修改为你的后端 WebSocket 地址

export default function ThreeScene() {
  const mountRef = useRef();
  const objectsRef = useRef({}); // 存储所有对象
  const sceneRef = useRef();
  const transitionRef = useRef();

  useEffect(() => {
    const scene = new THREE.Scene();
    sceneRef.current = scene;
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    mountRef.current.appendChild(renderer.domElement);

    camera.position.z = 10;

    // 初始化过渡效果
    transitionRef.current = fadeTransition(renderer);

    // 动画循环
    let animationId;
    const animate = () => {
      renderer.render(scene, camera);
      animationId = requestAnimationFrame(animate);
    };
    animate();

    // WebSocket 实时数据
    const socket = new window.WebSocket(WS_URL);
    socket.onmessage = async (event) => {
      try {
        let text;
        if (event.data instanceof Blob) {
          text = await event.data.text();
        } else {
          text = event.data;
        }
        const dataArr = JSON.parse(text); // 假设为数组
        dataArr.forEach(data => {
          let mesh = objectsRef.current[data.id];
          if (!mesh) {
            // 新对象
            let geometry, material;
            if (data.type === 'sphere') {
              geometry = new THREE.SphereGeometry(data.size || 1, 32, 32);
            } else if (data.type === 'cube') {
              geometry = new THREE.BoxGeometry(data.size || 1, data.size || 1, data.size || 1);
            } else {
              return; // 不支持的类型
            }
            material = new THREE.MeshBasicMaterial({ color: data.color || '#00ff00' });
            mesh = new THREE.Mesh(geometry, material);
            mesh.userData = { id: data.id };
            scene.add(mesh);
            objectsRef.current[data.id] = mesh;
            // 为新对象添加过渡效果
            transitionRef.current.startTransition(mesh);
          }
          // 更新属性
          const oldPosition = mesh.position.clone();
          const newPosition = new THREE.Vector3(data.x, data.y, data.z);
          
          // 如果位置变化较大，添加过渡效果
          if (oldPosition.distanceTo(newPosition) > 0.1) {
            transitionRef.current.startTransition(mesh);
          }
          
          mesh.position.copy(newPosition);
          if (data.color) mesh.material.color.set(data.color);
          if (data.size) {
            const newScale = data.size;
            if (Math.abs(mesh.scale.x - newScale) > 0.1) {
              transitionRef.current.startTransition(mesh);
            }
            mesh.scale.set(newScale, newScale, newScale);
          }
        });
        // 移除未在本次数据中的对象
        const ids = dataArr.map(d => d.id);
        Object.keys(objectsRef.current).forEach(id => {
          if (!ids.includes(id)) {
            const obj = objectsRef.current[id];
            transitionRef.current.startTransition(obj);
            setTimeout(() => {
              scene.remove(obj);
              delete objectsRef.current[id];
            }, 500);
          }
        });
      } catch (e) {
        console.error('Error processing WebSocket data:', e);
      }
    };

    // 交互：点击拾取
    const raycaster = new THREE.Raycaster();
    const mouse = new THREE.Vector2();
    function onClick(event) {
      const rect = renderer.domElement.getBoundingClientRect();
      mouse.x = ((event.clientX - rect.left) / rect.width) * 2 - 1;
      mouse.y = -((event.clientY - rect.top) / rect.height) * 2 + 1;
      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(Object.values(objectsRef.current));
      if (intersects.length > 0) {
        const obj = intersects[0].object;
        transitionRef.current.startTransition(obj);
        alert('Clicked object id: ' + obj.userData.id);
      }
    }
    renderer.domElement.addEventListener('click', onClick);

    // 清理
    return () => {
      cancelAnimationFrame(animationId);
      renderer.dispose();
      if (renderer.domElement && mountRef.current) {
        mountRef.current.removeChild(renderer.domElement);
      }
      socket.close();
      renderer.domElement.removeEventListener('click', onClick);
    };
  }, []);

  return <div ref={mountRef} style={{ width: '100vw', height: '100vh' }} />;
}
